def hamm_dist(seq1, seq2):
    '''
    Calculates the Hamming distance between 2 
    sequences and returns the number of point mutations

    Input params:
    seq1, seq2: nucleotide sequences for comparisons

    Output params:
    A: Hamming distance
    '''
    seq1_lst = list(seq1)
    seq2_lst = list(seq2)

    A = 0
    for (i,j) in zip(seq1_lst, seq2_lst):
        if i != j:
            A = A +1
    
    return A

seq1 = ""
seq2 = ""

print(hamm_dist(seq1, seq2))